// 기본 정의 생성기 (데이터가 없는 경우 사용)
function getDefinition(word, lang) {
    if (wordDefinitions[word] && wordDefinitions[word][lang]) {
        return wordDefinitions[word][lang];
    }
    // 데이터가 없으면 한국어 정의를 찾고, 그것도 없으면 '정의 없음' 표시
    if (wordDefinitions[word] && wordDefinitions[word]["한국어"]) {
        return wordDefinitions[word]["한국어"] + ` (${lang} 번역 준비 중)`;
    }
    return `${word}에 대한 설명입니다.`;
}

// 페이지 렌더링 함수
function renderWordPage(topic) {
    const currentLang = localStorage.getItem('selectedLanguage') || '한국어';
    
    // 페이지 제목 설정
    const pageHeader = document.querySelector('.page-header h1');
    if (pageHeader) pageHeader.textContent = topic;
    document.title = `${topic} - 단어장`;

    const listContainer = document.querySelector('.word-list-container');
    if (!listContainer) return;

    listContainer.innerHTML = '';
    const words = wordDatabase[topic] || [];

    words.forEach(word => {
        const item = document.createElement('div');
        item.className = 'word-list-item';
        
        const definition = getDefinition(word, currentLang);

        item.innerHTML = `
            <div class="word-text">
                <strong>${word}</strong><br>
                <span class="word-definition">${definition}</span>
            </div>
        `;
        listContainer.appendChild(item);
    });
}